    private void disabledAllModules()
    {
        this.moduleReceivers.forEach(IModuleReceiver::sentWorkingDisabled);
    }

    private void enabledAllModules()
    {
        this.moduleReceivers.forEach(IModuleReceiver::sentWorkingEnabled);
    }

    private boolean checkModules()
    {
        return getModuleCount() <= getMaxModules();
    }

    private int getModuleCount()
    {
        return moduleReceivers.size();
    }

    private int getMaxModules()
    {
        if (orbitTier == 1)
            return 6;
        if (orbitTier == 2)
            return 12;
        if (orbitTier == 3 && isExtended)
            return 15;
        if (orbitTier == 4 && isExtended)
            return 18;
        if (orbitTier == 5 && isExtended)
            return 24;
        return 12;
    }

    protected boolean isExtended()
    {
        return this.isExtended;
    }

    protected void setExtended(boolean extended)
    {
        this.isExtended = extended;
        invalidateStructure();
        reinitializeStructurePattern();
    }

